import{useEffect,useState} from 'react';
import axios from 'axios'

import './App.css';
// import ReactSearchBox from "react-search-box";
import { MDBInputGroup, MDBInput, MDBIcon, MDBBtn } from 'mdb-react-ui-kit';
import 'bootstrap/dist/css/bootstrap.min.css'

function App() {

  const[users,setUsers]=useState([""]);
  useEffect(()=>{
    // const json = response.json();
    // console.log("aaaaaaaaaaa");
    axios.get('http://localhost:5000/threads')
    .then(async(users)=>{
    await setUsers(users.data);
    await console.log(users);
    })
    .catch(err=>console.log(err))
     console.log(users);
  },[])


  const approvalfunction=async(event)=>{
    console.log(event);
    const userData={
      email:event
    };
    axios.post("http://localhost:5000/threads/add", userData).then((response) => {
      const message=response.data.msgg;
      // console.log(response.data.msgg);
    });

    // const response = await fetch("/threads/add", {
    //   method: "POST",
    //   body: JSON.stringify({ email: event }),
    //   headers: {
    //     "Content-Type": "application/json",
    //   },
    // });
    // if(response.ok)
    // {
    //   alert("approved successfully");
    // }
  }

  return (
    <div w-100 vh-100 d-flex justify-content-center align-items-center>
      <MDBInputGroup>
      <MDBInput label='Search' />
      <MDBBtn rippleColor='dark'>
        <MDBIcon icon='search' />
      </MDBBtn>
    </MDBInputGroup>
      <div className='w-50'>
      <table className='table'>
        <thead>
          <tr>
            <th>
              Id:
            </th>
            <th>
              Email:
            </th>
            <th>
              Name:
            </th>
          </tr>
        </thead>
        <tbody>
          {
            users.map(user=>{
              return <tr>
                <td>{user._id}</td>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td><button onClick={()=>approvalfunction(user.email)}>approved</button></td>
              </tr>
            })
          }
        </tbody>
      </table>
      </div>

    </div>
  );
}

export default App;
