import{useEffect,useState} from 'react';
import axios from 'axios'
import { MDBDataTable } from 'mdbreact';
import { MDBDatatable, MDBBtn, MDBIcon } from 'mdb-react-ui-kit';


import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css'

function App() {

  const[users,setUsers]=useState([""]);
  const[find,setFind]=useState([""]);
  const[searchuser,setSearchuser]=useState([""]);
  useEffect(()=>{
    fetchData();
    // axios.get('http://localhost:5000/threads')
    // .then(async(user)=>{
    // const a=await setUsers(user.data);
    // if(users!==find)
    // {
    //   setFind(user.data);
    // }
    // })
    // .catch(err=>console.log(err));
    // console.log(users);
  },[find])

  const fetchData = () => {
    axios.get('http://localhost:5000/threads')
      .then(async(user) => {
        await setUsers(user.data);
        if (users !== find) {
          setFind(user.data);
        }
      })
      .catch((err) => console.log(err));
  };




  const approvalfunction=async(event)=>{
    console.log(event);
    const userData={
      email:event
    };
    axios.post("http://localhost:5000/threads/add", userData).then((response) => {
      // const message=response.data.msgg;
      alert(response.data.msgg);
      fetchData();
      console.log(response.data.msgg);
    });


  }

  const deletefunction=async(event)=>{
    console.log(event);
    const userData={
      email:event
    };
    axios.post("http://localhost:5000/threads/del", userData).then((response) => {
      // const message=response.data.msgg;
      alert(response.data.msgg);
      fetchData();
      console.log(response.data.msgg);
    });


  }

  const data = {
    columns: [
      {
        label: 'Id',
        field: '_id',
        sort: 'asc',
        width: 150
      },
      {
        label: 'Name',
        field: 'name',
        sort: 'asc',
        width: 270
      },
      {
        label: 'Email',
        field: 'email',
        sort: 'asc',
        width: 200
      },
      {
        label: 'UPI',
        field: 'UPI_id',
        sort: 'asc',
        width: 200
      },
      {
        label: 'Events',
        field: 'selectedEvents',
        sort: 'asc',
        width: 200
      },
      {
        label: 'Workshop',
        field: 'selectedWorkshops',
        sort: 'asc',
        width: 200
      },
      {
        label: 'Approve',
        field: 'contact',
        width:200
      }

    ],
    rows:[...users].map((row) => {
      return {
        ...row,
        contact: (
          <div className="button-container"><button className="btn btn-success" onClick={() =>approvalfunction(`${row.email}`)}>Add</button>
          <button className="btn btn-danger" color='red' onClick={() =>deletefunction(`${row.email}`)}>Del</button>


          </div>
        ),
      };
    }),



  };

  return (
    <div>
      <br></br>
      <MDBDataTable
      striped
      bordered
      small
      btn
      data={data}
    />
     <div className='idcard' style={{ position: "absolute", left: "-9999px", top: "-9999px" }}>
          {/* <Download /> */}
        </div>
    </div>

  );
}

export default App;
